<?php abstract class Morgenshtern_Db_Table_Abstract extends Zend_Db_Table_Abstract{    /**     * Constructor.     *     * @return void     */    public function __construct($config = array())    {        $multiDb = Zend_Registry::get( 'multiDb' );		parent::__construct( $config );		$this->_setAdapter( $multiDb );    }    /**     *      *     * @return string     */    public function convert( $string, $reverse = false )    {        return $string;        if ( $reverse ) {			return iconv( 'utf-8', 'windows-1251//IGNORE', $string );		} else {			return iconv( 'windows-1251', 'utf-8//IGNORE', $string );		}    }}